='LC2022'><br/></div><div class='line' id='LC2023'>	<span class="sd">/**</span></div><div class='line' id='LC2024'><span class="sd">	 * Execute lessphp on a .less file or a lessphp cache structure</span></div><div class='line' id='LC2025'><span class="sd">	 * </span></div><div class='line' id='LC2026'><span class="sd">	 * The lessphp cache structure contains information about a specific</span></div><div class='line' id='LC2027'><span class="sd">	 * less file having been parsed. It can be used as a hint for future</span></div><div class='line' id='LC2028'><span class="sd">	 * calls to determine whether or not a rebuild is required.</span></div><div class='line' id='LC2029'><span class="sd">	 * </span></div><div class='line' id='LC2030'><span class="sd">	 * The cache structure contains two important keys that may be used</span></div><div class='line' id='LC2031'><span class="sd">	 * externally:</span></div><div class='line' id='LC2032'><span class="sd">	 * </span></div><div class='line' id='LC2033'><span class="sd">	 * compiled: The final compiled CSS</span></div><div class='line' id='LC2034'><span class="sd">	 * updated: The time (in seconds) the CSS was last compiled</span></div><div class='line' id='LC2035'><span class="sd">	 * </span></div><div class='line' id='LC2036'><span class="sd">	 * The cache structure is a plain-ol&#39; PHP associative array and can</span></div><div class='line' id='LC2037'><span class="sd">	 * be serialized and unserialized without a hitch.</span></div><div class='line' id='LC2038'><span class="sd">	 * </span></div><div class='line' id='LC2039'><span class="sd">	 * @param mixed $in Input</span></div><div class='line' id='LC2040'><span class="sd">	 * @param bool $force Force rebuild?</span></div><div class='line' id='LC2041'><span class="sd">	 * @return array lessphp cache structure</span></div><div class='line' id='LC2042'><span class="sd">	 */</span></div><div class='line' id='LC2043'>	<span class="k">public</span> <span class="k">static</span> <span class="k">function</span> <span class="nf">cexecute</span><span class="p">(</span><span class="nv">$in</span><span class="p">,</span> <span class="nv">$force</span> <span class="o">=</span> <span class="k">false</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC2044'><br/></div><div class='line' id='LC2045'>		<span class="c1">// assume no root</span></div><div class='line' id='LC2046'>		<span class="nv">$root</span> <span class="o">=</span> <span class="k">null</span><span class="p">;</span></div><div class='line' id='LC2047'><br/></div><div class='line' id='LC2048'>		<span class="k">if</span> <span class="p">(</span><span class="nb">is_string</span><span class="p">(</span><span class="nv">$in</span><span class="p">))</span> <span class="p">{</span></div><div class='line' id='LC2049'>			<span class="nv">$root</span> <span class="o">=</span> <span class="nv">$in</span><span class="p">;</span></div><div class='line' id='LC2050'>		<span class="p">}</span> <span class="k">elseif</span> <span class="p">(</span><span class="nb">is_array</span><span class="p">(</span><span class="nv">$in</span><span class="p">)</span> <span class="k">and</span> <span class="nb">isset</span><span class="p">(</span><span class="nv">$in</span><span class="p">[</span><span class="s1">&#39;root&#39;</span><span class="p">]))</span> <span class="p">{</span></div><div class='line' id='LC2051'>			<span class="k">if</span> <span class="p">(</span><span class="nv">$force</span> <span class="k">or</span> <span class="o">!</span> <span class="nb">isset</span><span class="p">(</span><span class="nv">$in</span><span class="p">[</span><span class="s1">&#39;files&#39;</span><span class="p">]))</span> <span class="p">{</span></div><div class='line' id='LC2052'>				<span class="c1">// If we are forcing a recompile or if for some reason the</span></div><div class='line' id='LC2053'>				<span class="c1">// structure does not contain any file information we should</span></div><div class='line' id='LC2054'>				<span class="c1">// specify the root to trigger a rebuild.</span></div><div class='line' id='LC2055'>				<span class="nv">$root</span> <span class="o">=</span> <span class="nv">$in</span><span class="p">[</span><span class="s1">&#39;root&#39;</span><span class="p">];</span></div><div class='line' id='LC2056'>			<span class="p">}</span> <span class="k">elseif</span> <span class="p">(</span><span class="nb">isset</span><span class="p">(</span><span class="nv">$in</span><span class="p">[</span><span class="s1">&#39;files&#39;</span><span class="p">])</span> <span class="k">and</span> <span class="nb">is_array</span><span class="p">(</span><span class="nv">$in</span><span class="p">[</span><span class="s1">&#39;files&#39;</span><span class="p">]))</span> <span class="p">{</span></div><div class='line' id='LC2057'>				<span class="k">foreach</span> <span class="p">(</span><span class="nv">$in</span><span class="p">[</span><span class="s1">&#39;files&#39;</span><span class="p">]</span> <span class="k">as</span> <span class="nv">$fname</span> <span class="o">=&gt;</span> <span class="nv">$ftime</span> <span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC2058'>					<span class="k">if</span> <span class="p">(</span><span class="o">!</span><span class="nb">file_exists</span><span class="p">(</span><span class="nv">$fname</span><span class="p">)</span> <span class="k">or</span> <span class="nb">filemtime</span><span class="p">(</span><span class="nv">$fname</span><span class="p">)</span> <span class="o">&gt;</span> <span class="nv">$ftime</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC2059'>						<span class="c1">// One of the files we knew about previously has changed</span></div><div class='line' id='LC2060'>						<span class="c1">// so we should look at our incoming root again.</span></div><div class='line' id='LC2061'>						<span class="nv">$root</span> <span class="o">=</span> <span class="nv">$in</span><span class="p">[</span><span class="s1">&#39;root&#39;</span><span class="p">];</span></div><div class='line' id='LC2062'>						<span class="k">break</span><span class="p">;</span></div><div class='line' id='LC2063'>					<span class="p">}</span></div><div class='line' id='LC2064'>				<span class="p">}</span></div><div class='line' id='LC2065'>			<span class="p">}</span></div><div class='line' id='LC2066'>		<span class="p">}</span> <span class="k">else</span> <span class="p">{</span></div><div class='line' id='LC2067'>			<span class="c1">// TODO: Throw an exception? We got neither a string nor something</span></div><div class='line' id='LC2068'>			<span class="c1">// that looks like a compatible lessphp cache structure.</span></div><div class='line' id='LC2069'>			<span class="k">return</span> <span class="k">null</span><span class="p">;</span></div><div class='line' id='LC2070'>		<span class="p">}</span></div><div class='line' id='LC2071'><br/></div><div class='line' id='LC2072'>		<span class="k">if</span> <span class="p">(</span><span class="nv">$root</span> <span class="o">!==</span> <span class="k">null</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC2073'>			<span class="c1">// If we have a root value which means we should rebuild.</span></div><div class='line' id='LC2074'>			<span class="nv">$less</span> <span class="o">=</span> <span class="k">new</span> <span class="nx">lessc</span><span class="p">(</span><span class="nv">$root</span><span class="p">);</span></div><div class='line' id='LC2075'>			<span class="nv">$out</span> <span class="o">=</span> <span class="k">array</span><span class="p">();</span></div><div class='line' id='LC2076'>			<span class="nv">$out</span><span class="p">[</span><span class="s1">&#39;root&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nv">$root</span><span class="p">;</span></div><div class='line' id='LC2077'>			<span class="nv">$out</span><span class="p">[</span><span class="s1">&#39;compiled&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nv">$less</span><span class="o">-&gt;</span><span class="na">parse</span><span class="p">();</span></div><div class='line' id='LC2078'>			<span class="nv">$out</span><span class="p">[</span><span class="s1">&#39;files&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nv">$less</span><span class="o">-&gt;</span><span class="na">allParsedFiles</span><span class="p">();</span></div><div class='line' id='LC2079'>			<span class="nv">$out</span><span class="p">[</span><span class="s1">&#39;updated&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nb">time</span><span class="p">();</span></div><div class='line' id='LC2080'>			<span class="k">return</span> <span class="nv">$out</span><span class="p">;</span></div><div class='line' id='LC2081'>		<span class="p">}</span> <span class="k">else</span> <span class="p">{</span></div><div class='line' id='LC2082'>			<span class="c1">// No changes, pass back the structure</span></div><div class='line' id='LC2083'>			<span class="c1">// we were given initially.</span></div><div class='line' id='LC2084'>			<span class="k">return</span> <span class="nv">$in</span><span class="p">;</span></div><div class='line' id='LC2085'>		<span class="p">}</span></div><div class='line' id='LC2086'><br/></div><div class='line' id='LC2087'>	<span class="p">}</span></div><div class='line' id='LC2088'><br/></div><div class='line' id='LC2089'>	<span class="k">static</span> <span class="k">protected</span> <span class="nv">$cssColors</span> <span class="o">=</span> <span class="k">array</span><span class="p">(</span></div><div class='line' id='LC2090'>		<span class="s1">&#39;aliceblue&#39;</span> <span class="o">=&gt;</span> <span class="s1">&#39;240,248,255&#39;</span><span class="p">,</span></div><div class='line' id='LC2091'>		<span class="s1">&#39;antiquewhite&